('Cimetière du Père-Lachaise', 'Plus grand cimetière de Paris avec tombes de célébrités dans un décor romantique.', 'quartier', 'https://upload.wikimedia.org/wikipedia/commons/thumb/3/3e/P%C3%A8re-Lachaise_-_Division_69_-_All%C3%A9e_transversale_n%C2%B01_01.jpg/1280px-P%C3%A8re-Lachaise_-_Division_69_-_All%C3%A9e_transversale_n%C2%B01_01.jpg', 'Paris', '20', 'gratuit', NULL, ARRAY['cimetière', 'célébrités', 'romantique', 'promenade'], 'https://pere-lachaise.com/'),
('Place Vendôme', 'Place octogonale prestigieuse avec colonne Vendôme et joailliers de luxe.', 'quartier', 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/d7/Place_Vend%C3%B4me%2C_Paris_1er%2C_Northwest_view_20140102_1.jpg/1280px-Place_Vend%C3%B4me%2C_Paris_1er%2C_Northwest_view_20140102_1.jpg', 'Paris', '1', 'gratuit', NULL, ARRAY['luxe', 'joaillerie', 'napoléon', 'élégance'], NULL),
('Le Marais', 'Quartier historique avec hôtels particuliers, musées, boutiques branchées et communauté LGBT.', 'quartier', 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/f3/Place_des_Vosges_Paris_Mai_2006_002.jpg/1280px-Place_des_Vosges_Paris_Mai_2006_002.jpg', 'Paris', '4', 'gratuit', NULL, ARRAY['historique', 'shopping', 'lgbt', 'vosges'], NULL),
