INSERT INTO activities (name, description, category, image_url, location, arrondissement, price_range, duration, tags, website) VALUES
('Sacré-Cœur (Basilique)', 'Basilique romano-byzantine dominant Paris depuis la butte Montmartre, accès gratuit.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/c5/Le_sacre_coeur.jpg/1024px-Le_sacre_coeur.jpg', 'Paris', '18', 'gratuit', '1h', ARRAY['basilique', 'montmartre', 'vue', 'religieux'], 'https://www.sacre-coeur-montmartre.com/'),
('Sacré-Cœur (Dôme)', 'Montée au dôme pour une vue panoramique exceptionnelle sur Paris à 360°.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/c5/Le_sacre_coeur.jpg/1024px-Le_sacre_coeur.jpg', 'Paris', '18', 'euro', '1h30', ARRAY['panorama', 'montmartre', 'vue', 'dôme'], 'https://www.sacre-coeur-montmartre.com/'),
('Invalides (Extérieurs)', 'Promenade gratuite autour du monument et de son esplanade avec vue sur le dôme doré.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/0/08/Les_Invalides%2C_Paris%2C_France.jpg/1280px-Les_Invalides%2C_Paris%2C_France.jpg', 'Paris', '7', 'gratuit', '1h', ARRAY['napoléon', 'dôme', 'militaire', 'promenade'], 'https://www.musee-armee.fr/'),
('Tour Eiffel (Sans montée)', 'Admirer la Dame de Fer depuis le Champ de Mars et le Trocadéro, gratuitement.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a8/Tour_Eiffel_Wikimedia_Commons.jpg/800px-Tour_Eiffel_Wikimedia_Commons.jpg', 'Tour Eiffel', '7', 'gratuit', '30 minutes', ARRAY['tour-eiffel', 'iconique', 'photo', 'gratuit'], 'https://www.toureiffel.paris/'),
('Tour Eiffel 2ème étage (Ascenseur)', 'Montée en ascenseur jusqu''au 2ème étage avec vue spectaculaire sur Paris.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a8/Tour_Eiffel_Wikimedia_Commons.jpg/800px-Tour_Eiffel_Wikimedia_Commons.jpg', 'Tour Eiffel', '7', 'euro2', '1h30', ARRAY['tour-eiffel', 'vue', 'ascenseur', 'incontournable'], 'https://www.toureiffel.paris/'),
('Tour Eiffel 2ème étage (Escalier)', 'Montée sportive à pied pour une expérience unique avec accès aux piliers historiques.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a8/Tour_Eiffel_Wikimedia_Commons.jpg/800px-Tour_Eiffel_Wikimedia_Commons.jpg', 'Tour Eiffel', '7', 'euro', '2h', ARRAY['tour-eiffel', 'sport', 'escalier', 'économique'], 'https://www.toureiffel.paris/'),
('Conciergerie', 'Prison révolutionnaire dans le Palais de la Cité, dernière demeure de Marie-Antoinette.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/9e/La_Conciergerie_de_nuit.jpg/1280px-La_Conciergerie_de_nuit.jpg', 'Paris', '1', 'euro', '1h', ARRAY['révolution', 'prison', 'médiéval', 'histoire'], 'http://www.paris-conciergerie.fr/'),
('Panthéon (Extérieurs)', 'Admirez le monument néoclassique abritant les grands Hommes depuis la place du Panthéon.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/80/Pantheon_of_Paris_007.JPG/1280px-Pantheon_of_Paris_007.JPG', 'Panthéon', '5', 'gratuit', '30 minutes', ARRAY['néoclassique', 'grands-hommes', 'quartier-latin', 'architecture'], 'http://www.paris-pantheon.fr/'),
('Panthéon (Visite)', 'Visite du monument avec crypte des personnalités illustres et pendule de Foucault.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/80/Pantheon_of_Paris_007.JPG/1280px-Pantheon_of_Paris_007.JPG', 'Panthéon', '5', 'euro', '2h', ARRAY['crypte', 'foucault', 'histoire', 'grands-hommes'], 'http://www.paris-pantheon.fr/'),
('Assemblée Nationale', 'Visite gratuite du Palais Bourbon, siège de la démocratie française depuis 1798.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/3/3e/Assembl%C3%A9e_Nationale_%28Paris%29.JPG/1280px-Assembl%C3%A9e_Nationale_%28Paris%29.JPG', 'Paris', '7', 'gratuit', '1h30', ARRAY['politique', 'démocratie', 'palais-bourbon', 'gratuit'], 'https://www.assemblee-nationale.fr/'),
('Opéra Garnier (Sans guide)', 'Visite libre du chef-d''œuvre de Charles Garnier avec grand escalier et plafond Chagall.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/c9/Palais_Garnier%27s_grand_fa%C3%A7ade%2C_2010.jpg/1280px-Palais_Garnier%27s_grand_fa%C3%A7ade%2C_2010.jpg', 'Paris', '9', 'euro', '1h30', ARRAY['opéra', 'garnier', 'chagall', 'architecture'], 'https://www.operadeparis.fr/'),
('Opéra Garnier (Avec guide)', 'Visite guidée approfondie révélant les secrets et l''histoire du Palais Garnier.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/c/c9/Palais_Garnier%27s_grand_fa%C3%A7ade%2C_2010.jpg/1280px-Palais_Garnier%27s_grand_fa%C3%A7ade%2C_2010.jpg', 'Paris', '9', 'euro2', '1h30', ARRAY['opéra', 'garnier', 'guidée', 'secrets'], 'https://www.operadeparis.fr/'),
('Arc de Triomphe (Extérieurs)', 'Admirer le monument napoléonien et la flamme du Soldat inconnu gratuitement.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/79/Arc_de_Triomphe%2C_Paris_21_October_2010.jpg/1024px-Arc_de_Triomphe%2C_Paris_21_October_2010.jpg', 'Paris', '8', 'gratuit', '30 minutes', ARRAY['napoléon', 'étoile', 'champs-élysées', 'monument'], NULL),
('Arc de Triomphe (Visite)', 'Montée sur la terrasse panoramique offrant une vue à 360° sur Paris et ses avenues.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/7/79/Arc_de_Triomphe%2C_Paris_21_October_2010.jpg/1024px-Arc_de_Triomphe%2C_Paris_21_October_2010.jpg', 'Paris', '8', 'euro2', '1h', ARRAY['panorama', 'napoléon', 'vue', 'champs-élysées'], 'http://www.paris-arc-de-triomphe.fr/'),
('Sainte-Chapelle', 'Joyau gothique rayonnant avec vitraux du XIIIe siècle exceptionnels.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/9/98/Sainte_Chapelle_-_Upper_Chapel.jpg/800px-Sainte_Chapelle_-_Upper_Chapel.jpg', 'Paris', '1', 'euro', '1h', ARRAY['gothique', 'vitraux', 'île-de-la-cité', 'chef-d-oeuvre'], 'http://www.sainte-chapelle.fr/'),
('Notre-Dame (Sans montée)', 'Visite gratuite de la cathédrale restaurée après l''incendie de 2019.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Notre-Dame_de_Paris%2C_4_October_2017.jpg/1024px-Notre-Dame_de_Paris%2C_4_October_2017.jpg', 'Île de la Cité', '4', 'gratuit', '1h', ARRAY['cathédrale', 'gothique', 'gratuit', 'histoire'], 'https://www.notredamedeparis.fr/'),
('Notre-Dame (Avec montée tours)', 'Ascension des tours pour voir les gargouilles et la vue sur Paris.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Notre-Dame_de_Paris%2C_4_October_2017.jpg/1024px-Notre-Dame_de_Paris%2C_4_October_2017.jpg', 'Île de la Cité', '4', 'euro', '2h', ARRAY['cathédrale', 'tours', 'gargouilles', 'panorama'], 'https://www.notredamedeparis.fr/'),
('Tour Montparnasse', 'Vue panoramique à 210m de haut, la meilleure vue sur Paris avec la Tour Eiffel.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/f/f5/Tour_Montparnasse_from_the_Tour_Eiffel%2C_4_July_2009.jpg/800px-Tour_Montparnasse_from_the_Tour_Eiffel%2C_4_July_2009.jpg', 'Quartier Montparnasse', '15', 'euro2', '1h', ARRAY['panorama', 'vue', 'moderne', 'terrasse'], 'https://www.tourmontparnasse56.com/'),
('BnF Richelieu', 'Bibliothèque historique avec salle de lecture ovale magnifique et expositions.', 'tourisme_historique', 'https://upload.wikimedia.org/wikipedia/commons/thumb/8/8e/BnF_Richelieu_-_salle_ovale_2.jpg/1280px-BnF_Richelieu_-_salle_ovale_2.jpg', 'Paris', '2', 'euro', '1h30', ARRAY['bibliothèque', 'architecture', 'culture', 'livres'], 'https://www.bnf.fr/');
